package br.com.dba.timesheet.pojo;import java.io.Serializable;import java.util.Date;/** * Classe responsável por guardar a sessao do usuario. O cliente no momento de * login obtém uma instância desta classe para realizar a verficação de login * nos métodos do controle de acesso. (Ex: verificarAcao) *  */public class Sessao implements Serializable {    /**     * Comment for <code>serialVersionUID</code>     */    private static final long serialVersionUID = 1L;    private java.util.Date _dataLogon;    private java.lang.String _indicaAtiva;    private java.util.Date _dataLogoff;        private java.lang.Integer _codigoPerfil;        private Usuario _usuario;            public Sessao() {    }    public Sessao(Date _dataLogon, String _indicaAtiva) {        this.setDataLogon(_dataLogon);        this.setIndicaAtiva(_indicaAtiva);    }       /**     *      * Return the value associated with the column: DataLogon     *      */    public java.util.Date getDataLogon() {        return _dataLogon;    }    /**     *      * Set the value related to the column: DataLogon     *      * @param _dataLogon     *            the DataLogon value     *      */    public void setDataLogon(java.util.Date _dataLogon) {        this._dataLogon = _dataLogon;    }    /**     *      * Return the value associated with the column: IndicaAtiva     *      */    public java.lang.String getIndicaAtiva() {        return _indicaAtiva;    }        public java.lang.Integer getCodigoPerfil() {		return _codigoPerfil;	}	public void setCodigoPerfil(java.lang.Integer perfil) {		_codigoPerfil = perfil;	}    	/**     *      * Set the value related to the column: IndicaAtiva     *      * @param _indicaAtiva     *            the IndicaAtiva value     *      */    public void setIndicaAtiva(java.lang.String _indicaAtiva) {        this._indicaAtiva = _indicaAtiva;    }    /**     *      * Return the value associated with the column: DataLogoff     *      */    public java.util.Date getDataLogoff() {        return _dataLogoff;    }    /**     *      * Set the value related to the column: DataLogoff     *      * @param _dataLogoff     *            the DataLogoff value     *      */    public void setDataLogoff(java.util.Date _dataLogoff) {        this._dataLogoff = _dataLogoff;    }    public Usuario getUsuario() {        return this._usuario;    }    public void setUsuario(Usuario _usuario) {        this._usuario = _usuario;    }}